INSERT INTO public.book( author, book_state, date, description, isbn, publisher, quant, title) VALUES ('Jarosław Faron','DELETE','1972-06-04','Pierwsza książka Nassima Nicholasa Taleba, autora słynnego "Czarnego Łabędzia" i "Antykruchości". W błyskotliwym eseju autor zastanawia się nad rolą przypadku na rynkach finansowych, i szerzej – w życiu człowieka. Na podstawie swego wieloletniego doświadczenia zawodowego praktyka-ilościowca, a także w oparciu o setki przeczytanych książek, Taleb snuje frapującą opowieść o tym, jak funkcjonować w świecie, w którym kluczową rolę odgrywa losowość i dlaczego ludzie tak usilnie starają się usystematyzować przypadek. W swych rozważaniach przechodzi od matematycznej','5d2380bd-307c-4a90-ada4-a9e2e3f5fa0d','Grupo Planeta','57',' Blaszany bębenek ');
