INSERT INTO public.book( author, book_state, date, description, isbn, publisher, quant, title) VALUES ('Stanisław Majchrzak','NOTRESERVED','1972-06-04','        W błyskotliwym eseju autor zastanawia się nad rolą przypadku na rynkach finansowych, i szerzej – w życiu człowieka. Na podstawie swego wieloletniego doświadczenia zawodowego praktyka-ilościowca, a także w oparciu o setki przeczytanych książek, Taleb snuje frapującą opowieść o tym, jak funkcjonować w świecie, w którym kluczową rolę odgrywa losowość i dlaczego ludzie tak usilnie starają się usystematyzować przypadek. W swych rozważaniach przechodzi od matematycznej metody Monte Carlo (wykorzystywanej do modelowania matematycznego złożonych procesów, w których trudno za pomocą podejścia analitycznego przewidzieć wynik) do koncepcji rosyjskiej ruletki.','623dea06-f324-4278-89ac-d482206e53be','Hachette Livre','28',' Wichrowe Wzgórza Emily Brontë');
