INSERT INTO public.book( author, book_state, date, description, isbn, publisher, quant, title) VALUES ('Sylwia Kryński','DELETE','1972-06-04','Trzecia część początku kultowego cyklu zekranizowanego jako kultowy serial telewizyjny Pamiętnik Stefano przenosi akcję o sto lat wcześniej i odkrywa początek historii miłosnego trójkąta dwóch braci wampirów i pięknej dziewczyny, której obaj pragną Damon zdradził brata w Nowym Orleanie. Teraz Stefano postanawia rozpocząć wszystko od nowa. Ślubuje nie skrzywdzić już nigdy więcej człowieka. Przemierza ulice Manhattanu, usiłując zniknąć w tętniącym życiem mieście. A kiedy myśli, że uciekł już','0767a6ab-9fd4-49c3-9a1a-172cac681091','Random House','13',' Metamorfozy ');
